    execution = runner.start_execution(SCENE_CAPTURE_IMAGE_METADATA)
    params = execution.params(params)
    cargs = scene_capture_image_cargs(params, execution)
    ret = scene_capture_image_outputs(params, execution)
    execution.run(cargs)
    return ret


